const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
}; // this object will never change and it doesn't depend on anything that is in the component. So we place it here completely outside of the component. So now this object will not have to be generated by JS each time the component is rendered. It will be created only once when the module is loaded.

// Below is style for the stars container.
const starsContainerStyle = {
  display: "flex",
  gap: "4px",
};

const textStyle = {
  lineHeight: "1",
  margin: "0",
};

// we want to allow the user of this component to set the maximum amount of stars(so, the maximum rating). So we pass a maxRating  prop to the component and we use it in Array.from() to define length
export default function StarRating({ maxRating = 5 }) {
  return (
    <div style={containerStyle}>
      <div style={starsContainerStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <span>S{i + 1}</span>
        ))}
      </div>
      <p style={textStyle}>10</p>
    </div>
  );
}
