import { useState } from "react";

const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
}; // this object will never change and it doesn't depend on anything that is in the StarRating component. So we place it here completely outside of the component. So now this object will not have to be generated by JS each time the StarRating component is rendered. It will be created only once when the module is loaded.

// Below is style for the stars container.
const starsContainerStyle = {
  display: "flex",
};

const textStyle = {
  lineHeight: "1",
  margin: "0",
};

// we want to allow the user of this StarRating component to set the maximum amount of stars(so, the maximum rating). So we pass a maxRating prop to the StarRating component and we use it in Array.from() to define length. We pass maxRating = 5 in the StarRating component as prop to set a default value of 5 stars in case the user does not set the max amount of stars
export default function StarRating({ maxRating = 5 }) {
  const [rating, setRating] = useState(0); // this state is used because whenever we click on one of the stars, then we want to display the current rating in the paragraph element. We need to listen for click event on each of the stars so we define a onRate prop in each of the <Star /> component genereated from Array.from() and we will later receive inside the Star component function and pass it in OnClick prop

  return (
    <div style={containerStyle}>
      <div style={starsContainerStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <Star key={i} onRate={() => setRating(i + 1)} />
        ))}
      </div>
      <p style={textStyle}>{rating || ""}</p>
      {/* If rating is a falsy value(i.e. 0) then we will display empty string(i.e.) nothing on the screen */}
    </div>
  );
}

const starStyle = {
  width: "48px",
  height: "48px",
  display: "block",
  cursor: "pointer",
};

function Star({ onRate, full }) {
  return (
    <span role="button" style={starStyle} onClick={onRate}>
      {
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="#000"
          stroke="#000"
        >
          <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
        </svg>
      }
    </span>
  );
}

