import { useEffect, useState } from "react";

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

const KEY = "f608407f";

export default function App() {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // now whenever movie data is still being loaded in the background, we want to instead display some loading indicator. So in case movie data loading takes time, we see a flash where there is no content. So the movie data was loading but hadn't arrived yet. So now in the mean time we want to display some loading indicator. So in order to do that we have this state which tells UI that data is still being loaded and then as soon as the data has been loaded, we want to display that data and not that indicator anymore. In our effect, right before the fetching actually starts, we setIsLoading to true.
  const [error, setError] = useState(""); // we want to display the new error message that we throw below in case an error happens while fetching data, onto the screen instead of the Loading indicator. So this state is for that. It tells currently whether we have an error or not. Here it not a boolean but truly the error message. Based on this error state, we will conditionally render the ErrorMessage component which receives a messgae prop with some message that it will then display on the screen.
  const [selectedId, setSelectedId] = useState(null); // we need to select a movie and display some data related to it. Selecting happens on left box on dcreena nd displaying happens on right box on screen. So we create this state here in the parent component. Here we only store the id and not the entire movie object itself because the movies that we get here from the search very limited(we only get data about the title, year and the poster). On the right side we will want all kinds of details that are not included in the first search. So there would have to be another API call which will load the movie again and then we will get all the details and this movie will be fetched based on the ID that we get in the array we get when we search a movie(we can see this array by doing console.log(data.Search))

  // const tempQuery = "interstellar";

  /*
  // BELOW IS JUST AND EXPERIMENT WITH DEPENDENCY ARRAY OF EFFECT:
  // useEffect(function () {
  //   console.log("After initial render");
  // }, []);

  // useEffect(function () {
  //   console.log("After every render");
  // });

  // console.log("During render"); // we can see that on console, first During render is logged, then After initial render, then After every render. the reason is that effects only run after the Browser paint while the render logic runs during render. So console.log("During render") is executed first(so during the render of this component) and then we have After initial render and then After every render simply because After initial render appears first in the code. After it if we type any letter in the Search bar, we update the query state and hence the component is re-renered and hence consolelog("During render") was executed due to which we see During render first and then After every render because the effect that logs After every render has no dependency array which means that effect is synchronized with everything and therefore it runs on every render. The effect that logs After initial render is synchronized with no variables(which is the meaning of the empty array) and therefore that effect was not executed as the component was re-rendered with the query state.

  // BELOW IS ANOTHER EXPERIMENT WITH DEPENDENCY ARRAY OF EFFECT:
  // useEffect(function () {
  //   console.log("After initial render");
  // }, []);

  // useEffect(function () {
  //   console.log("After every render");
  // });

  // useEffect(
  //   function () {
  //     console.log("D");
  //   },
  //   [query]
  // );

  // console.log("During render"); // now, once we type a letter in search bar, first During render is logged, then After every render and then D. Now we get D logged because then effect logging it is synchronized with the query state variable. Therefore the query just changed and the effect synchronized with state was executed and logged D.
*/

  // At times of fetching data and dealing with asynchronous data, we need to assume that somethings can go wrong(like internet connection breaking while fetching data or no movie found for the search query) and therefore we also need to account for those situations by handling those errors.
  useEffect(
    function () {
      async function fetchMovies() {
        try {
          setIsLoading(true); // this tells UI that loadind is happening and we can then render the indicator over there. Then after everything is finished, we set isLoading state back to false as seen towards the end in the finally{} block in this function
          setError(""); // before we start fetching we also set the error to an empty string because if we do not do this and if we get an error at some point and we generate an error message on the screen(login for catching and setting error done below), then the next time we try to fetch data and display on screen, fetching will be done correctly(if no error occurs) but displaying cannot be done and still error is displayed on screen because we we never reset the error.

          const res = await fetch(
            `http://www.omdbapi.com/?apiKey=${KEY}&s=${query}`
          );

          if (!res.ok)
            throw new Error("Something went wrong with fetching movies!"); // now in between fetching data, user's internet connection might break so app will always keep showing the Loading indicator making user beleive that data will arrive at some point. so when it happens we want to display an error message on screen. Now reacting to errors like this is not built into the fetch() function itself, we need to do it manually. Now here on the response object that we receive from the fetch() exists one ok property. So basically if the response is not ok, then we want to throw a new error. Now if we throw an error here, we need to wrap all our code into a try catch block.

          const data = await res.json();
          // console.log(data);

          if (data.Response === "False") throw new Error("Movie not found !"); // if for a query, no movie is found, then the data that comes back from this API is undefined. So we no longer have the Search property now(So we don't have data.Search). So what happens is that data.Search is being set to undefined. So now if we log the received data on cosole, we see an object with Response and Error properties. So we can use Response(which in this case is False) to check if the movie is not found and throw an Error. This if here and not anywhere else because we check it after we have received the data from API.

          setMovies(data.Search);
          // console.log(movies); // here if we try to log the movies to the console, we get an empty array as a result. This happens becuase setting state is asynchronous. So after setting state in the just above line using setMovies(), it doesn't mean that it happens immediately. So it will happen after the function has been called. so right here in console.log(movies), we have stale state. So before, movies was just an empty array which gets logged. After some time we get the correct output. Now if we do this console.log() again, we get correct output. Now, the reson for getting two outputs is because of React's strict mode. So when strict mode is active in React 18, our effects will run not only once, but twice(but only in development. So when our application is in production, this will no longer be happening). This happens so REact can identify if there are some problems in our effects.
        } catch (err) {
          console.error(err.message); // err.message is the string that pass into throw new Error('') as above.
          setError(err.message); // in case of error, we set the error state to the error string that we passed above while throwing error. Now based on this error state, we will conditionally render the ErrorMessage component which receives a messgae prop with some message that it will then display on the screen.
        } finally {
          setIsLoading(false); // This is here because is we the error is thrown above, then the code after it in the try{} block will not be executed. So it is in this finally{} block which is always executed. Now in the returned JSX, we conditionally render the <Loader.> component if isLoading is true, but there are 2 more conditional renders with it. (Look and understand. if not understood, Watch Lecture 146 - Adding a loading state and Lecture 147 - Handling errors)
        }
      }

      if (!query.length) {
        setMovies([]); // here we set movies back to an empty array(so basically removing all the movies from the UI)
        setError(""); // here we reset the error back to nothing
        return;
      } // if we don't do this if if block, then when have no search query, we see Movie not found on screen(even on opening application) which is not really true(It is actually true because the API actually searched for a movie with an empty string but in a situation when we have no query we actually don't even want to search). Also, doing this if block, the fetchMovies function will not even be called(as called in the just below line).

      fetchMovies(); // here we just call the function
    },
    [query]
  ); // useEffects() doesn't return anything so we don't store the resuukt into any variable but instead we pass in a function and this function is then called our effect and it contains the code that we want to run as a side effect(so basically that we want to register as a side effect to be executed at a certain point in time). We also need to pass in a second argumnent which is a dependency array. We'll learn about it as we proceed further. empty array as here means that the effect that we just specified here will only run on mount. Now ew can see that there are no infinite loops or requests. So now effect is only running as the component mounts. Now, whenever we need a lot of code to handle a promise, it's a lot easier and nicer to just have an async function. However if we write async nefore the function inside useEffect() here and use await inside it, we get an ESLint warning which tells us that effect callbacks are synchronous to prevent race conditions. So basically the effect function that we placed into useEffect() cannot return a promise which is what an async function does. So instead of doing diretly like this, we create a new function and place the async function there as we did here

  // // We know we should never update state in render logic.
  // // Basic idea is to fetch some movie data as soon as this App component here mounts for the very  first time.
  // fetch(`http://www.omdbapi.com/?apiKey=${KEY}&s=interstellar`)
  //   .then((res) => res.json())
  //   .then((data) => console.log(data.Search)); // Now, this data fetching that we are doing here is actually introducing a side effect into the component's render logic. So it is clearly an interaction with an outside world which should never be allowed in render logic. Now, as we see here, if insetead of logging the fetched data on console, we set state here with the fetched data, in the Network tab we can see that it is running an infinite number of requests and it never really stops. So every second, our app is firing off multiple requests to this API. This happends because setting the state here in the render logic will then immediately cause the component to re-render itself again. However, as the component is re-rendered, the function here is executed again, which in turn will fetch again, which in turn will set the movies again as well and this whole thing starts over ad over again. So this is the reason why it is really not allowed to set state in the render logic. Now, we do actually need to set the movies here but without all the problems. So for that we need the useEffect() hoosk.

  // // we see another example just below where wo use setWatched:
  // // setWatched([[]]); // We did setWatched here immediately in the top level code to some empty array and then actually we do get a real error. We get the error of too many re-renders. It's because of this state setting right here in setWatched(). So if we are really setting state here in the top without being inside a .then() handler, then immediately REact will complain that there are too many renders which means we again entered that infinite loop where updating the state will cause a component to re-render, which will cause the state to be set, ad so on to infinity.

  // so we can useEffect() hook for above. The idea of it is to give us a place where we can safely write side effects like the above one. But side effects registered with useEffect() hook will only be excuted after certain renders(for example, only write after initial render which is exactly what we are looking for in this situation).
  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery} />
        <NumResults movies={movies} />
      </NavBar>
      <Main>
        {/* <Box element={<MovieList movies={movies} />} />
        <Box
          element={
            <>
              <WatchedSummary watched={watched} />
              <WatchedMoviesList watched={watched} />
            </>
          }
        /> */}
        {/* Above is prop drilling solution using explicity prop (element in this case. It
            can be anything. So we pass element as prop instead of children in the Box
            component and conditionally render that element prop). In the 2nd <Box />
            component above, we are passing a brand new piece of JSX, so here now we actually
            need a fragment */}

        {/* Below is prop drilling solution using children prop */}

        <Box>
          {/* {isLoading ? <Loader /> : <MovieList movies={movies} />} */}
          {isLoading && <Loader />}
          {!isLoading && !error && <MovieList movies={movies} />}
          {error && <ErrorMessage message={error} />}
        </Box>

        <Box>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
        </Box>
      </Main>
    </>
  );
}

function Loader() {
  return <p className="loader">Loading...</p>;
}

function ErrorMessage({ message }) {
  return (
    <p className="error">
      <span>⛔</span> {message}
    </p>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🍿</span>
      <h1>usePopcorn</h1>
    </div>
  );
}

function Search({ query, setQuery }) {
  // const [query, setQuery] = useState("");

  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  // instead of using children prop to solve prop drilling, we can also use an explicit pro
  // (like element we used here instead of children)

  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "–" : "+"}
      </button>
      {isOpen && children}
    </div>
  );
}

/*
function WatchedBox() {
  const [watched, setWatched] = useState(tempWatchedData);
  const [isOpen2, setIsOpen2] = useState(true);

  return (
    <div className="box">
      <button
        className="btn-toggle"
        onClick={() => setIsOpen2((open) => !open)}
      >
        {isOpen2 ? "–" : "+"}
      </button>
      {isOpen2 && (
        <>
          <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
        </>
      )}
    </div>
  );
}
*/

function MovieList({ movies }) {
  // const [movies, setMovies] = useState(tempMovieData); // we need this state to
  //                               dynamically calculate search results found for displaying
  //                               in NumResults component and the state lives here in this
  //                               MovieList component so we lift this movies state up from
  //                               here to the common parent component which is App component
  //                               and from there pass the movies state as prop to the Main
  //                               component and then to the ListBox component and then to
  //                               this MovieList component and from the App component we
  //                               also send the movies state down to Navbar component and
  //                               then to NumResults component inside which we dynamically
  //                               display the Number of Search results by doing
  //                               movies.length through prop drilling. This prop drilling
  //                               can be tedious if components are very deeply nested then
  //                               we have to pass the prop through a lot of components. A
  //                               solution of this problem of prop drilling is Component
  //                               Composition. Component composition is combining
  //                               different components using the children prop or
  //                               explicitly defined props. Ex as just below:
  //
  //                               function Modal({ children }){
  //                                 return (
  //                                   <div className="modal">
  //                                     {children}
  //                                   >/div>
  //                                 )
  //                               }
  //
  //                               function Error() {
  //                                 return <p>This went wrong!</p>
  //                               }
  //
  //                               Now with Component composition we can simply do:
  //                               <Modal>
  //                                 <Error />
  //                               </Modal>
  //
  //                               We can use component composition:
  //                               1. When we want to create highly reusable and
  //                                  flexible components (such as the modal window)
  //                               2. To fix prop drilling problem (great for layouts)
  //
  //                               Now we use component composition to fix component
  //                               drilling problem where we pass movies prop into the
  //                               NavBar component and then from there into the
  //                               NumResults component. Here we only have one leve of prop
  //                               drilling. If we could use NumResults component right in
  //                               the App component instead of the NavBar component, then
  //                               we wouldn't have to pass the movies prop into the NavBar
  //                               which will fix that prop drilling problem. So we do this
  //                               with component composition as above. So we pass children
  //                               prop in the NavBar component function and inside it, write
  //                               {children} in the returned <nav></nav> element after the
  //                               <Logo/> component and in the App component, instead of
  //                               just <Navbar/> we write it like an HTML element and inside
  //                               it write <Search/> and <NumResults/> as:
  //
  //                               <NavBar>
  //                                 <Search />
  //                                 <NumResults movies={movies} />
  //                               </NavBar>
  //
  //                               Similarly we do for <Main/> component and for any further
  //                               component if needed
  return (
    <ul className="list">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} />
      ))}
    </ul>
  );
}

function Movie({ movie }) {
  return (
    <li>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  );
}

function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));

  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgImdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}

function WatchedMoviesList({ watched }) {
  return (
    <ul className="list">
      {watched.map((movie) => (
        <WatchedMovie movie={movie} key={movie.imdbID} />
      ))}
    </ul>
  );
}

