import { useState } from "react";

const containerStyle = {
  display: "flex",
  alignItems: "center",
  gap: "16px",
}; // this object will never change and it doesn't depend on anything that is in the StarRating component. So we place it here completely outside of the component. So now this object will not have to be generated by JS each time the StarRating component is rendered. It will be created only once when the module is loaded.

// Below is style for the stars container.
const starsContainerStyle = {
  display: "flex",
};

// we want to allow the user of this StarRating component to set the maximum amount of stars(so, the maximum rating). So we pass a maxRating prop to the StarRating component and we use it in Array.from() to define length. We pass maxRating = 5 in the StarRating component as prop to set a default value of 5 stars in case the user does not set the max amount of stars. We also improve reusability with props(i.e. defining a public API for consumers to use it (Watch Lecture 118 - Props as a component API for better understanding. Also lecture 119 - Improvong reusability with props))
export default function StarRating({
  maxRating = 5,
  color = "#fcc419",
  size = 48,
}) {
  const [rating, setRating] = useState(0); // this state is used because whenever we click on one of the stars, then we want to display the current rating in the paragraph element. We need to listen for click event on each of the stars so we define a onRate prop in each of the <Star /> component genereated from Array.from() and we will later receive inside the Star component function and pass it in OnClick prop. If we click a star, then from staring upto that star, all stars should be full and rest of the stars should be empty. So, in the Star component function, we receive a full prop which will tell if the star is full and we conditionally render the full and empty stars based on it.
  const [tempRating, setTempRating] = useState(0); // this state is for hover because on hover we temporarirly get a rating corresponding to the star we are hovering on and also accordingly the stars appear full or empty. rating and full stars get fixed only on clicking. We handle this hover event in the Star component function by passing a handler function through onHoverIn prop and onHoverOut prop in the Star component in Array.from() and receive them in the Star component function and use inside a onMouseEnter prop and also a onMouseLeave prop.

  function handleRating(rating) {
    setRating(rating);
  }

  return (
    <div style={containerStyle}>
      <div style={starsContainerStyle}>
        {Array.from({ length: maxRating }, (_, i) => (
          <Star
            key={i}
            onRate={() => handleRating(i + 1)}
            full={tempRating ? tempRating >= i + 1 : rating >= i + 1} // this full prop needs to be a true or false value for conditional rendering of full or empty stars in the Star component function. so for each star, current rating is compared with star index + 1. if rating >= index + 1 for a star then that star is full. Also, on hovering, only tempRating works and when we click then full and empty stars get fixed. So, if there is tempRating, then it works otherwise rating works. So if we click on a star then full and empty stars and correct rating are shown accordingly but if we hover again then again tempRating is there so tempRating now works and on hovering, the stars from starting upto that star appear hovered and the rating that should be if we click that star appear but if we stop hovering on any star, then again the fixed full and empty stars with the fixed rating that was done before due to click appear again. Similarly where we display rating, we do tempRating || rating || ""
            onHoverIn={() => setTempRating(i + 1)} // here we just have to set the tempRating to the rating of the current star on hover.
            onHoverOut={() => setTempRating(0)} // on hover out, we set tempRating back to initial value
          />
        ))}
      </div>
      <p style={textStyle}>{tempRating || rating || ""}</p>
      {/* If rating is a falsy value(i.e. 0) then we will display empty string(i.e.) nothing on the screen */}
    </div>
  );
}

const starStyle = {
  width: "48px",
  height: "48px",
  display: "block",
  cursor: "pointer",
};

function Star({ onRate, full, onHoverIn, onHoverOut }) {
  return (
    <span
      role="button"
      style={starStyle}
      onClick={onRate}
      onMouseEnter={onHoverIn}
      onMouseLeave={onHoverOut}
    >
      {full ? (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="#000"
          stroke="#000"
        >
          <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
        </svg>
      ) : (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke="#000"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="{2}"
            d="M11.049 2.927c.3-.921 1.603-.921 1.902 0l1.519 4.674a1 1 0 00.95.69h4.915c.969 0 1.371 1.24.588 1.81l-3.976 2.888a1 1 0 00-.363 1.118l1.518 4.674c.3.922-.755 1.688-1.538 1.118l-3.976-2.888a1 1 0 00-1.176 0l-3.976 2.888c-.783.57-1.838-.197-1.538-1.118l1.518-4.674a1 1 0 00-.363-1.118l-3.976-2.888c-.784-.57-.38-1.81.588-1.81h4.914a1 1 0 00.951-.69l1.519-4.674z"
          />
        </svg>
      )}
    </span>
  );
}

